const { createCanvas, loadImage } = require('canvas')
const { wrapText } = require('../functions/canvas')

async function burn(burn) {
    if (!burn) throw new Error(`Please provide something to be burned.`)

    const base = await loadImage(`https://raw.githubusercontent.com/caseyCantCode/discordImageGenImages/main/spongebob-burn.png`);
    const canvas = createCanvas(base.width, base.height);
    const ctx = canvas.getContext('2d');
    ctx.drawImage(base, 0, 0);
    ctx.fillStyle = 'black';
    ctx.textBaseline = 'top';
    ctx.font = '35px Arial';
    let fontSize = 35;
    while (ctx.measureText(burn).width > 400) {
        fontSize--;
        ctx.font = `${fontSize}px Arial`;
    }
    const lines = await wrapText(ctx, burn, 180);
    ctx.fillText(lines.join('\n'), 55, 103);
    return canvas.toBuffer()
}
module.exports = burn