const { createCanvas, loadImage } = require('canvas')

async function cursedspongebob(amount) {
    if (!amount || isNaN(amount)) throw new Error(`Please provide a valid amount.`)

    if (amount < 1 || amount > 100) throw new Error(`Please provide a valid amount that is higher than 1 and les than 100.`)

    const sponge = await loadImage(`https://raw.githubusercontent.com/caseyCantCode/discordImageGenImages/main/cursed-sponge.png`);
    const rows = Math.ceil(amount / 10);
    const canvas = createCanvas(sponge.width * (rows > 1 ? 10 : amount), sponge.height * rows);
    const ctx = canvas.getContext('2d');
    let width = 0;
    for (let i = 0; i < amount; i++) {
        const row = Math.ceil((i + 1) / 10);
        ctx.drawImage(sponge, width, sponge.height * (row - 1));
        if ((width + sponge.width) === (sponge.width * (rows > 1 ? 10 : amount))) width = 0;
        else width += sponge.width;
    }
    return canvas.toBuffer()
}
module.exports = cursedspongebob