const { createCanvas, loadImage } = require('canvas');
const request = require('node-superfetch');
const { desaturate, contrast } = require('../functions/canvas');

async function deepfry(image) {
    if (!image) throw new Error(`Please provide a image url.`)

    try {
        const { body } = await request.get(image);
        const data = await loadImage(body);
        const canvas = createCanvas(data.width, data.height);
        const ctx = canvas.getContext('2d');
        ctx.drawImage(data, 0, 0);
        desaturate(ctx, -20, 0, 0, data.width, data.height);
        contrast(ctx, 0, 0, data.width, data.height);
        const attachment = canvas.toBuffer('image/jpeg', { quality: 0.2 });
        if (Buffer.byteLength(attachment) > 8e+6) throw new Error(`Resulting image was above 8 MB.`)
        return canvas.toBuffer()
    } catch (err) {
        throw new Error(`Sorry there was an error: ${err}`)
    }
}
module.exports = deepfry