const request = require("node-superfetch")
const Canvas = require('canvas')

async function dislike(image) {
    if (!baseAvatarURL) throw new Error(`Please provide a valid image url.`)

    const { body } = await request.get("https://cdn.discordapp.com/attachments/793653432761843713/794754859411701770/dislikeit.png")
    const template = await Canvas.loadImage(body)

    let canvas = Canvas.createCanvas(parseInt(template.width), parseInt(template.height))

    let ctx = canvas.getContext("2d")

    let avatar = await Canvas.loadImage(baseAvatarURL)

    ctx.drawImage(template, 0, 0, canvas.width, canvas.height)

    ctx.drawImage(avatar, 0, 0, 540, 540)
    return canvas.toBuffer()
}
module.exports = dislike