const { createCanvas, loadImage } = require('canvas')
const { drawImageWithTint } = require('../functions/canvas');
const request = require('node-superfetch');

async function helivesinyou(avatarURL) {
    if (!avatarURL) throw new Error(`Please provide a image url.`)

    try {
        const base = await loadImage(`https://cdn.discordapp.com/attachments/813970833147953175/818305142340190218/he-lives-in-you.png`)
        const { body } = await request.get(avatarURL);
        const avatar = await loadImage(body);
        const canvas = createCanvas(base.width, base.height);
        const ctx = canvas.getContext('2d');
        ctx.drawImage(base, 0, 0);
        ctx.rotate(-24 * (Math.PI / 180));
        drawImageWithTint(ctx, avatar, '#00115d', 75, 160, 130, 150);
        ctx.rotate(24 * (Math.PI / 180));
        return canvas.toBuffer()
    } catch (err) {
        throw new Error(`Sorry there was an error: ${err}`)
    }
}
module.exports = helivesinyou