const { createCanvas, loadImage } = require('canvas');
const request = require('node-superfetch');

async function heman(avatarURL) {
    if (!avatarURL) throw new Error(`Please provide a valid image url.`)

    try {
        const base = await loadImage(`https://cdn.discordapp.com/attachments/813970833147953175/818312091500150804/i-have-the-power.png`);
        const { body } = await request.get(avatarURL);
        const avatar = await loadImage(body);
        const canvas = createCanvas(base.width, base.height);
        const ctx = canvas.getContext('2d');
        ctx.drawImage(base, 0, 0);
        ctx.rotate(18.3 * (Math.PI / 180));
        ctx.drawImage(avatar, 332, -125, 175, 175);
        ctx.rotate(-18.3 * (Math.PI / 180));
        return canvas.toBuffer()
    } catch (err) {
        throw new Error(`Sorry there was an error: ${err}`)
    }
}
module.exports = heman