const { createCanvas, loadImage } = require(`canvas`);
const { wrapText } = require('../functions/canvas');

async function lisa(text) {
    if (!text) throw new Error(`Please provide a message.`)

    const base = await loadImage(`https://raw.githubusercontent.com/caseyCantCode/discordImageGenImages/main/lisa-presentation.png`);
    const canvas = createCanvas(base.width, base.height);
    const ctx = canvas.getContext(`2d`);
    ctx.drawImage(base, 0, 0);
    ctx.textAlign = `center`;
    ctx.textBaseline = `top`;
    ctx.font = `40px Arial`;
    let fontSize = 40;
    while (ctx.measureText(text).width > 1320) {
        fontSize -= 1;
        ctx.font = `${fontSize}px Arial`;
    }
    const lines = await wrapText(ctx, text, 330);
    const topMost = 185 - (((fontSize * lines.length) / 2) + ((20 * (lines.length - 1)) / 2));
    for (let i = 0; i < lines.length; i++) {
        const height = topMost + ((fontSize + 20) * i);
        ctx.fillText(lines[i], base.width / 2, height);
    }

    return canvas.toBuffer();
}
module.exports = lisa