const { shortenText } = require('../functions/canvas')
const { createCanvas, loadImage } = require('canvas');

async function strongpassword(weak, strong) {
    if (!weak || !strong) throw new Error(`Please provide a weak password and a strong password.`)

    const base = await loadImage(`https://raw.githubusercontent.com/caseyCantCode/discordImageGenImages/main/new-password.png`);
    const canvas = createCanvas(base.width, base.height);
    const ctx = canvas.getContext('2d');
    ctx.drawImage(base, 0, 0);
    ctx.font = `60px Arial`
    ctx.fillText(shortenText(ctx, weak, 780), 70, 191);
    ctx.fillText(shortenText(ctx, strong, 780), 70, 667);
    return canvas.toBuffer()
}
module.exports = strongpassword