const { centerImagePart } = require('../functions/canvas')
const request = require('node-superfetch')
const { createCanvas, loadImage } = require('canvas');

async function thisguy(image) {
    if (!image) throw new Error(`Please provide a image url.`)

    try {
        const base = await loadImage(`https://raw.githubusercontent.com/caseyCantCode/discordImageGenImages/main/this-guy.png`);
        const { body } = await request.get(image);
        const data = await loadImage(body);
        const canvas = createCanvas(base.width, base.height);
        const ctx = canvas.getContext('2d');
        ctx.drawImage(base, 0, 0);
        const { x, y, width, height } = centerImagePart(data, 361, 361, 76, 62);
        ctx.drawImage(data, x, y, width, height);
        return canvas.toBuffer()
    } catch (e) {
        throw new Error(`Sorry there was an error: ${e}`)
    }
}
module.exports = thisguy