const { createCanvas, loadImage } = require('canvas')
const request = require('node-superfetch');
const { centerImagePart } = require('../functions/canvas')

async function worthless(image) {
    if (!image) throw new Error(`Please provide a image url.`)

    try {
        const base = await loadImage("https://cdn.discordapp.com/attachments/816809738632167434/818164248903024660/woahitsworthless.png");
        const { body } = await request.get(image);
        const data = await loadImage(body);
        const canvas = createCanvas(base.width, base.height);
        const ctx = canvas.getContext('2d');
        ctx.drawImage(base, 0, 0);
        ctx.rotate(6 * (Math.PI / 180));
        const center1 = centerImagePart(data, 400, 400, 496, 183);
        ctx.drawImage(data, center1.x, center1.y, center1.width, center1.height);
        ctx.rotate(-6 * (Math.PI / 180));
        ctx.translate(canvas.width / 2, canvas.height / 2);
        ctx.rotate(160 * (Math.PI / 180));
        ctx.translate(-(canvas.width / 2), -(canvas.height / 2));
        const center2 = centerImagePart(data, 75, 75, 625, 55);
        ctx.drawImage(data, center2.x, center2.y, center2.width, center2.height);
        ctx.rotate(-160 * (Math.PI / 180));
        return canvas.toBuffer()
    } catch (err) {
        throw new Error(`Sorry there was an error: ${err}`)
    }
}
module.exports = worthless